// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

interface ICanvasContract {
	function addPixel(uint256 tokenId, address owner) external;
}

contract Choice is ERC721URIStorage {
	uint256 public tokenCounter;
	ICanvasContract public canvasContract;

	constructor(address _canvasContract) ERC721("Choice", "OCA") {
		tokenCounter = 0;
		canvasContract = ICanvasContract(_canvasContract);
	}

	function mintToken(string[] memory grid) public {
		require(grid.length == 40, "Grid must be 8x5 (40 cells)");

		string memory svg = generateSVG(grid);
		string memory imageURI = svgToImageURI(svg);
		string memory tokenURI = formatTokenURI(imageURI);

		_safeMint(msg.sender, tokenCounter);
		_setTokenURI(tokenCounter, tokenURI); // This now works because we inherit ERC721URIStorage

		// Notify the canvas contract to add a pixel
		canvasContract.addPixel(tokenCounter, msg.sender);

		tokenCounter++;
	}

	function generateSVG(
		string[] memory grid
	) internal pure returns (string memory) {
		string
			memory svgStart = "<svg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 80 50' width='80' height='50'>";
		string memory svgEnd = "</svg>";
		string memory svgContent = "";

		uint256 x; // Horizontal position
		uint256 y; // Vertical position
		uint256 cellSize = 10; // Each cell is 10x10 in size

		for (uint256 i = 0; i < grid.length; i++) {
			x = (i % 8) * cellSize;
			y = (i / 8) * cellSize;

			if (keccak256(bytes(grid[i])) != keccak256(bytes("0"))) {
				svgContent = string(
					abi.encodePacked(
						svgContent,
						"<rect width='10' height='10' x='",
						Strings.toString(x),
						"' y='",
						Strings.toString(y),
						"' fill='",
						grid[i],
						"'/>"
					)
				);
			}
		}

		return string(abi.encodePacked(svgStart, svgContent, svgEnd));
	}

	function svgToImageURI(
		string memory svg
	) internal pure returns (string memory) {
		string memory baseURL = "data:image/svg+xml;base64,";
		string memory svgBase64Encoded = base64(bytes(svg));
		return string(abi.encodePacked(baseURL, svgBase64Encoded));
	}

	function formatTokenURI(
		string memory imageURI
	) internal pure returns (string memory) {
		return
			string(
				abi.encodePacked(
					'{"name": "Choice Token", "description": "An SVG based on-chain art", "image": "',
					imageURI,
					'"}'
				)
			);
	}

	// Base64 encoding function
	function base64(bytes memory data) internal pure returns (string memory) {
		string
			memory TABLE = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
		string memory result = new string(4 * ((data.length + 2) / 3));

		assembly {
			let tablePtr := add(TABLE, 1)
			let resultPtr := add(result, 32)

			for {
				let i := 0
			} lt(i, mload(data)) {

			} {
				i := add(i, 3)
				let input := and(mload(add(data, i)), 0xffffff)

				let out := mload(add(tablePtr, and(shr(18, input), 0x3F)))
				out := shl(8, out)
				out := add(out, mload(add(tablePtr, and(shr(12, input), 0x3F))))
				out := shl(8, out)
				out := add(out, mload(add(tablePtr, and(shr(6, input), 0x3F))))
				out := shl(8, out)
				out := add(out, mload(add(tablePtr, and(input, 0x3F))))
				out := shl(224, out)

				mstore(resultPtr, out)
				resultPtr := add(resultPtr, 4)
			}

			switch mod(mload(data), 3)
			case 1 {
				mstore(sub(resultPtr, 2), shl(240, 0x3d3d))
			}
			case 2 {
				mstore(sub(resultPtr, 1), shl(248, 0x3d))
			}
		}

		return result;
	}
}
